"use strict";

import * as toolsys from '../path.ux/scripts/simple_toolsys.js';
import {Context, AppToolStack} from '../core/context.js';
import {initSimpleController} from '../path.ux/scripts/simple_controller.js';

toolsys.setContextClass(Context);

import {App} from '../editors/editor_base.js';
import {Library, DataBlock, DataRef} from '../core/lib_api.js';
import {IDGen} from '../util/util.js';
import * as util from '../util/util.js';
import {Vector3, Vector4, Vector2, Quat, Matrix4} from '../util/vectormath.js';
import {ToolOp, UndoFlags} from '../path.ux/scripts/simple_toolsys.js';
import {getDataAPI} from '../data_api/api_define.js';
import {View3D} from '../editors/view3d/view3d.js';
import {Scene} from '../core/scene.js';
import {BinaryReader, BinaryWriter} from '../util/binarylib.js';
import * as cconst from '../core/const.js';
import {AppSettings} from './settings.js';
import {SceneObject} from './sceneobject.js';
import {Mesh} from './mesh.js';
import {makeCube} from './mesh_shapes.js';
import '../path.ux/scripts/struct.js';
let STRUCT = nstructjs.STRUCT;

export class FileLoadError extends Error {};

//override default undo implementation in Path.ux's toolop class
ToolOp.prototype.undo_pre = function(ctx) {
  this._undo = ctx.appstate.createUndoFile();
}

ToolOp.prototype.undo = function(ctx) {
  ctx.appstate.loadUndoFile();
}

export class BasicFileOp extends ToolOp {
  constructor() {
    super();
  }
  
  exec(ctx) {
    let scene = new Scene();
    let lib = ctx.datalib;
    
    lib.add(scene);
    lib.getLibrary("scene").active = scene;
    
    let mesh = makeCube();
    lib.add(mesh);
    
    let ob = new SceneObject(mesh);
    
    lib.add(ob);    
    scene.add(ob);
    scene.objects.setSelect(ob, true);
  }
  
  static tooldef() {return {
    undoflag    : UndoFlags.IS_UNDO_ROOT | UndoFlags.NO_UNDO,
    uiname      : "File Start",
    toolpath    : "app.__new_file_basic"
  }}
};

export function genDefaultScreen(appstate) {
  appstate.screen.clear();
  
  let sarea = document.createElement("screenarea-x");
  
  sarea.ctx = appstate.ctx;
  sarea.switch_editor(View3D);
  
  sarea.pos[0] = sarea.pos[1] = 0.0;
  sarea.size[0] = appstate.screen.size[0];
  sarea.size[1] = appstate.screen.size[1];
  
  appstate.screen.appendChild(sarea);
  appstate.screen.listen();
  
  sarea.setCSS();
  sarea.area.setCSS();
}

export function genDefaultFile(appstate) {
  if (cconst.APP_KEY_NAME in localStorage) {
    let buf = localStorage[cconst.APP_KEY_NAME];

    try {
      buf = util.atob(buf);
      appstate.loadFile(buf.buffer);
    } catch (error) {
      util.print_stack(error);
      console.warn("Failed to load startup file");
    }

    return;
  }

  let tool = new BasicFileOp();
  
  genDefaultScreen(appstate);
  appstate.toolstack.execTool(tool);
}

export const BlockTypes = {
  SCREEN     : "scrn",
  DATABLOCK  : "dblk",
  SETTINGS   : "sett",
  LIBRARY    : "libr"
}

export class FileBlock {
  constructor(type, data) {
    this.type = type;
    this.data = data;
  }
}

export class FileData {
  constructor() {
    this.blocks = [];
    this.save_screen = undefined;
    this.load_screen = undefined;
  }
}

export class AppState {
  constructor() {
    this.settings = new AppSettings;
    this.ctx = new Context(this);
    this.toolstack = new AppToolStack(this.ctx);
    this.api = getDataAPI();
    this.screen = undefined;
    this.datalib = new Library();
  }
  
  start() {
    this.ctx = new Context(this);
    
    this.screen = document.createElement("webgl-app-x");
    this.screen.ctx = this.ctx;
    this.screen.size[0] = window.innerWidth-45;
    this.screen.size[1] = window.innerHeight-45;
    
    document.body.appendChild(this.screen);
    this.screen.setCSS();
    this.screen.listen();
    
    genDefaultFile(this);
    this.filename = "unnamed." + cconst.FILE_EXT;
  }
  
  createFile(args={save_screen : true, load_screen : true}) {
    let file = new BinaryWriter();
    
    file.string(cconst.FILE_MAGIC);
    file.uint16(cconst.APP_VERSION);
    file.uint16(0); //reserved for file flags (may compression?)
    
    let buf = nstructjs.write_scripts();
    
    file.int32(buf.length);
    file.bytes(buf);
    
    function writeblock(type, object) {
      if (type === undefined || type.length != 4) {
        throw new Error("bad type in writeblock: " + type);
      }
      
      file.string(type);
      let data = [];

      nstructjs.manager.write_object(data, object);
      
      file.int32(data.length);
      file.bytes(data);
    }
    
    if (args.save_screen) {
      writeblock(BlockTypes.SCREEN, this.screen);
    }
    
    writeblock(BlockTypes.LIBRARY, this.datalib);
    
    for (let lib of this.datalib.libs) {
      for (let block of lib) {
        let typeName = block.constructor.blockDefine().typeName;
        let data = [];
        
        file.string(BlockTypes.DATABLOCK);
        
        nstructjs.manager.write_object(data, block);
        let len = typeName.length + data.length + 4;
        
        file.int32(len);
        file.int32(typeName.length);
        file.string(typeName);
        file.bytes(data);
      }
    }
    
    return file.finish().buffer;
  }

  testFileIO() {
    let file = this.createFile();
    this.loadFile(file);
    window.redraw_viewport();
  }

  //expects an ArrayBuffer or a DataView
  loadFile(buf, args={load_screen : true, load_settings : false}) {
    let file = new BinaryReader(buf);
    
    let s = file.string(4);
    if (s !== cconst.FILE_MAGIC) {
      throw new FileLoadError("Not a valid file");
    }
    
    let version = file.uint16();
    let flag = file.uint16();
    
    let len = file.int32();
    let structs = file.string(len);
    
    let istruct = new nstructjs.STRUCT();
    
    istruct.parse_structs(structs);
    
    let screen;
    let datablocks = [];
    let datalib = undefined;

    console.log(file);

    while (!file.at_end()) {
      let type = file.string(4);
      let len = file.int32();
      
      let data = file.bytes(len);
      data = new DataView((new Uint8Array(data)).buffer);
      console.log("->", type);

      if (args.load_screen && type == BlockTypes.SCREEN) {
        screen = istruct.read_object(data, App);

        if (this.screen !== undefined) {
          this.screen.destroy();
          this.screen.remove();
        }

        this.screen = screen;
        this.screen.ctx = this.ctx;
        this.screen.listen();

        //push active area contexts
        for (let sarea of this.screen.sareas) {
          sarea.area.push_ctx_active();
          sarea.area.pop_ctx_active();
        }

        document.body.appendChild(screen);

        this.screen.update();
        this.screen.regenBorders();
        this.screen.setCSS();
      } else if (type == BlockTypes.LIBRARY) {
        datalib = istruct.read_object(data, Library);
        console.log("Found library");

        this.datalib.destroy();
        this.datalib = datalib;
      } else if (type == BlockTypes.DATABLOCK) {
        let file2 = new BinaryReader(data);
        
        let len = file2.int32();
        let clsname = file2.string(len);
        
        let cls = DataBlock.getClass(clsname);
        len = data.byteLength - len - 4;
        let data2 = file2.bytes(len);
        let block;
        
        if (cls === undefined) {
          console.warn("Warning, unknown block type", clsname);
          
          block = istruct.read_object(data2, DataBlock);
        } else {
          block = istruct.read_object(data2, cls);
        }
        
        datablocks.push([clsname, block]);
      } else if (args.load_settings && type == BlockTypes.SETTINGS) {
        let settings = istruct.read_object(data, AppSettings);
        
        this.settings.destroy();
        this.settings = settings;
      }
    }
    
    if (screen !== undefined) {
      screen.doOnce(() => {
        this.screen.on_resize([window.innerWidth, window.innerHeight]);
        this.screen.setCSS();
        this.screen.update();
      });
    }

    if (datalib === undefined) {
      throw new Error("failed to load file");
      return;
    }

    for (let dblock of datablocks) {
      this.datalib.getLibrary(dblock[0]).add(dblock[1]);
    }
    
    this.do_versions(version);
    
    datalib = this.datalib;
    function getblock(dataref) {
      return datalib.get(dataref);
    }
    
    function getblock_us(dataref, user) {
      let ret = getblock(dataref);
      
      if (ret !== undefined) {
        ret.lib_addUser(user);
      }
      
      return ret;
    }
    
    for (let lib of this.datalib.libs) {
      lib.dataLink(getblock, getblock_us);
    }
    
    this.do_versions_post(version);
  }

  saveStartupFile() {
    let buf = this.createFile();
    buf = util.btoa(buf);

    localStorage[cconst.APP_KEY_NAME] = buf;
    console.log(`saved startup file; ${(buf.length/1024).toFixed(2)}kb`);
  }

  do_versions(version, datalib) {
    
  }
  
  do_versions_post(version, datalib) {
  }
  
  createUndoFile() {
    let args = {
      save_screen : false,
      load_screen : false
    };
    
    return this.createFile(args);
  }
  
  destroy() {
    this.screen.unlisten();
  }
  
  draw() {
  }
};

export function init() {
  initSimpleController();
  window._appstate = new AppState();
  
  let animreq;
  let f = () => {
    animreq = undefined;
    
    _appstate.draw();
  }
  
  window.redraw_all = function() {
    if (animreq !== undefined) {
      return;
    }
    
    animreq = requestAnimationFrame(f);
  }
  
  _appstate.start();
}

export const APP_VERSION = 100; //major minor patch
export const APP_KEY_NAME = "webgl_app_framework1";
export const FILE_EXT = "w3d";
export const FILE_MAGIC = "WJ3D";

import '../path.ux/scripts/struct.js';
import {View3D} from '../editors/view3d/view3d.js';
import {NodeEditor} from '../editors/node/NodeEditor.js';
import {getContextArea} from '../editors/editor_base.js';
import * as util from '../util/util.js';
import {Mesh} from './mesh.js';
import {DataRef} from './lib_api.js';
import {ToolStack, UndoFlags} from '../path.ux/scripts/simple_toolsys.js';

export class ToolContext {
  constructor(appstate=_appstate) {
    this._appstate = appstate;
  }

  get toolstack() {
    return this._appstate.toolstack;
  }

  get api() {
    return this.state.api;
  }
  
  get state() {
    return this._appstate;
  }
  
  get datalib() {
    return this.state.datalib;
  }
  
  get scene() {
    return this.datalib.getLibrary("scene").active;
  }
  
  get object() {
    return this.scene.objects.active;
  }
  
  get mesh() {
    let ob = this.object;
    
    if (ob !== undefined) {
      return ob.data;
    }
  }

  get selectedObjects() {
    return this.scene.objects.selected.editable;
  }

  /**returns selected mesh objects,
    ignoring objects that use the same mesh
    instance (only one will get yield in that case)
   */
  get selectedMeshObjects() {
    let this2 = this;
    return (function*() {
      let visit = new util.set();

      for (let ob of this2.selectedObjects) {
        let bad = ob.data === undefined;
        bad = bad || !(ob.data instanceof Mesh);
        bad = bad || visit.has(ob.data);

        if (bad) {
          continue;
        }

        yield ob;
      }
    })();
  }
}

/** includes UI stuff that ToolOps can't use
 *  unless in modal mode
 */
export class Context extends ToolContext {
  get view3d() {
    return getContextArea(View3D);
  }

  get nodeEditor() {
    return getContextArea(NodeEditor);
  }
}

export class SavedContext extends ToolContext {
  constructor(ctx, datalib) {
    super(ctx.appstate);

    this._object = new DataRef();
    this._selectedObjects = [];
    this._selectedMeshObjects = [];
    this._scene = new DataRef();
    this._mesh = new DataRef();

    this.ctx = ctx;
  }

  lock() {
    this.ctx = undefined;
  }

  _getblock(key) {
    let key2 = "_" + key;
    if (this[key2].lib_id != -1) {
      return this.datalib.get(this[key2]);
    }

    if (this.ctx !== undefined) {
      this[key2] = DataRef.fromBlock(this.ctx[key]);
      return this[key];
    }
  }

  get scene() {
    return this._getblock("scene");
  }

  get object() {
    return this._getblock("object");
  }

  get mesh() {
    return this._getblock("mesh");
  }

  get selectedObjects() {
    if (this._selectedObjects.length > 0) {
      let ret = [];

      for (let ob of this._selectedObjects) {
        ret.push(this.datalib.get(ob));
      }

      return ret;
    }

    if (this.ctx === undefined) {
      return this._selectedObjects;
    }

    let ret = this._selectedObjects = [];

    for (let ob of this.ctx.selectedObjects) {
      ret.push(DataRef.fromBlock(ob));
    }

    return this.selectedObjects;
  }

  get selectedMeshObjects() {
    if (this._selectedMeshObjects.length > 0) {
      let ret = [];

      for (let ob of this._selectedMeshObjects) {
        ret.push(this.datalib.get(ob));
      }

      return ret;
    }

    if (this.ctx === undefined) {
      return this._selectedMeshObjects;
    }

    let ret = this._selectedMeshObjects = [];

    for (let ob of this.ctx.selectedMeshObjects) {
      ret.push(DataRef.fromBlock(ob));
    }

    return this.selectedMeshObjects;
  }
}
SavedContext.STRUCT = `
SavedContext {
  _scene               : DataRef;
  _mesh                : DataRef;
  _object              : DataRef;
  _selectedObjects     : array(DataRef);
  _selectedMeshObjects : array(DataRef);
}
`;
nstructjs.manager.add_class(SavedContext);

export class AppToolStack extends ToolStack {
  constructor(ctx) {
    super(ctx);
  }

  execTool(toolop, ctx=this.ctx) {
    let tctx = new SavedContext(ctx, ctx.datalib);

    if (!toolop.canRun(ctx)) {
      console.log("toolop.canRun returned false");
      return;
    }

    toolop.execCtx = tctx;

    if (!(toolop.undoflag & UndoFlags.NO_UNDO)) {
      this.cur++;

      //truncate
      this.length = this.cur+1;

      this[this.cur] = toolop;
      toolop.undoPre(tctx);
    }

    if (toolop.is_modal) {
      this.modal_running = true;

      toolop._on_cancel = (function(toolop) {
        this.pop_i(this.cur);
        this.cur--;
      }).bind(this);

      //will handle calling .exec itself
      toolop.modalStart(ctx);
    } else {
      toolop.exec(tctx);
    }
  }

  undo() {
    if (this.cur >= 0 && !(this[this.cur].undoflag & UndoFlags.IS_UNDO_ROOT)) {
      console.log("undo!", this.cur, this.length);

      let tool = this[this.cur];

      this[this.cur].undo(this.ctx);
      this.cur--;
      this.ctx.save();
    }
  }

  redo() {
    if (this.cur >= -1 && this.cur+1 < this.length) {
      console.log("redo!", this.cur, this.length);

      this.cur++;

      this[this.cur].undoPre(this.ctx);
      this[this.cur].exec(this.ctx);
      this.ctx.save();
    }
  }
}

import '../path.ux/scripts/struct.js';
import * as util from '../util/util.js';
let STRUCT = nstructjs.STRUCT;

export const CDFlags = {
  SELECT       : 1,
  SINGLE_LAYER : 2
};

export let CDElemMap = {};
export let CDElemTypes = [];

export class CustomDataElem {
  constructor() {
  }
  
  copyTo(b) {
    throw new Error("implement me");
  }
  
  copy() {
    throw new Error("implement me");
  }
    
  interp(dest, ws, datas) {
  }
  
  validate() {
    return true;
  }
  
  static define() {return {
    elemTypeMask : 0, //see MeshTypes in mesh.js
    typeName     : "typeName",
    uiTypeName   : "uiTypeName",
    defaultName  : "defaultName",
    //elemSize     : 3,
    flag         : 0
  }};
  
  static register(cls) {
    CDElemTypes.push(cls);
    CDElemMap[cls.define().typeName] = cls;
  }
  
  static getTypeClass(typeName) {
    return CDElemMap[typeName];
  }
}
CustomDataElem.STRUCT = `
mesh.CustomDataElem {
}
`;
nstructjs.manager.add_class(CustomDataElem);

export class CustomDataLayer {
  constructor(typename, name, flag, id) {
    this.elemTypeMask = 0;
    this.typeName = typename;
    this.name = name;
    this.flag = flag;
    this.id = id;
    this.index = 0; //index in flat list of layers in elements
  }
  
  static fromSTRUCT(CustomDataLayer) {
    let ret = new CustomDataLayer();
    reader(ret);
    return ret;
  }
}

CustomDataLayer.STRUCT = `
mesh.CustomDataLayer {
  typeName      : string;
  name          : string;
  flag          : int;
  id            : int;
  index         : int;
  elemTypeMask  : int;
}
`;
nstructjs.manager.add_class(CustomDataLayer);

export class LayerSet extends Array {
  constructor(typeName) {
    super();
    
    this.typeName = typeName;
    this.active = undefined;
    this.idmap = {};
  }
  
  push(layer) {
    super.push(layer);
    this.idmap[layer.id] = layer;
  }
  
  static fromSTRUCT(reader) {
    let ret = new LayerSet();
    reader(ret);
    
    for (let layer of ret._layers) {
      ret.push(layer);
    }
    
    if (ret.active >= 0) {
      ret.active = ret.idmap[ret.active];
    }
    delete ret._layers;
    
    return ret;
  }
}
LayerSet.STRUCT = `
mesh.LayerSet {
  _layers  : array(abstract(mesh.CustomDataLayer)) | obj;
  active   : int | obj.active !== undefined ? obj.active.id : undefined;
  typeName : string;
}
`;
nstructjs.manager.add_class(LayerSet);

export class CustomData {
  constructor() {
    this.layers = {};
    this.flatlist = [];
    this.on_layeradd = undefined;
    this.on_layerremove = undefined;
    this.idgen = new util.IDGen();
  }
  
  addLayer(cls, name=cls.define().defaultName) {
    let type = cls.define().typeName;
    let layer = new CustomDataLayer(type, name, undefined, this.idgen.next());
    let set = this.getLayerSet(type);
    
    layer.index = this.flatlist.length;
    this.flatlist.push(layer);
    
    layer.elemTypeMask = cls.define().elemTypeMask;
    
    set.push(layer);
    
    if (this.on_layeradd) {
      this.on_layeradd(layer, set);
    }
    return layer;
  }
  
  remLayer(layer) {
    let set = this.layers[layer.typeName];
    if (set.active === layer) {
      set.active = set.length > 1 ? set[(set.indexOf(layer)+1) % set.length] : undefined;
    }
    
    set.remove(layers);
    
    if (this.on_layerremove) {
      this.on_layerremove(layer, set);
    }
  }
  
  getLayerSet(typename) {
    if (!(typename in this.layers)) {
      this.layers[typename] = new LayerSet(typename);
      this.layers[typename].active = undefined;
    }
    
    return this.layers[typename];
  }
  
  static fromSTRUCT(CustomData) {
    let ret = new CustomData();
    reader(ret);
    
    let idmap = {};
    
    for (let layer of ret._layers) {
      ret.layers[layer.typeName] = layer;
      idmap[layer.id] = layer;
    }
      
    for (let i=0; i<ret.flatlist.length; i++) {
      ret.flatlist[i] = idmap[ret.flatlist[i]];
    }
    
    delete ret._layers;
    
    return ret;
  }
  
  _getLayers() {
    let ret = [];
    
    for (let k in this.layers) {
      ret.push(this.layers[k]);
    }
    
    return ret;
  }
}

CustomData.STRUCT = `
mesh.CustomData {
  _layers  : array(mesh.LayerSet) | obj._getLayers();
  flatlist : array(e, int) | e.id;
  idgen    : IDGen;
}
`;
nstructjs.manager.add_class(CustomData);

import * as util from '../util/util.js';
import {Vector2, Vector3, Vector4, Quat, Matrix4} from '../util/vectormath.js';
import * as simplemesh from './simplemesh.js';
import * as webgl from './webgl.js';

export class FBO {
  constructor(gl, width=512, height=512) {
    this.gl = gl;
    this.fbo = undefined;
    this.regen = true;
    this.size = [width, height];
    this.texDepth = undefined;
  }
  
  create(gl) {
    gl = this.gl = gl === undefined ? this.gl : gl;
    
    //console.trace("framebuffer creation");
    
    this.fbo = gl.createFramebuffer();
    this.texDepth = gl.createTexture();
    this.texColor = gl.createTexture();
    
    webgl.Texture.defaultParams(gl, this.texDepth);
    webgl.Texture.defaultParams(gl, this.texColor);

    gl.bindTexture(gl.TEXTURE_2D, this.texDepth);
    //let type = gl.depth_texture.UNSIGNED_INT_24_8_WEBGL;
    
    let type = gl.UNSIGNED_INT;
    gl.texImage2D(gl.TEXTURE_2D, 0, gl.DEPTH_COMPONENT, this.size[0], this.size[1], 0, gl.DEPTH_COMPONENT, type, null);
    
    gl.bindTexture(gl.TEXTURE_2D, this.texColor);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.NEAREST);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.NEAREST);

    gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, this.size[0], this.size[1], 0, gl.RGBA, gl.FLOAT, null);
    
    gl.bindFramebuffer(gl.FRAMEBUFFER, this.fbo);
    
    gl.framebufferTexture2D(gl.FRAMEBUFFER, gl.DEPTH_ATTACHMENT, gl.TEXTURE_2D, this.texDepth, 0);
    gl.framebufferTexture2D(gl.FRAMEBUFFER, gl.COLOR_ATTACHMENT0, gl.TEXTURE_2D, this.texColor, 0);
  }
  
  bind(gl) {
    gl = this.gl = gl === undefined ? this.gl : gl;
    
    if (this.regen) {
      this.regen = 0;
      this.create();
    }
    
    gl.bindFramebuffer(gl.FRAMEBUFFER, this.fbo);
  }

  unbind(gl) {
    gl.bindFramebuffer(gl.FRAMEBUFFER, null);
  }

  destroy() {
    if (this.fbo !== undefined) {
      this.gl.deleteFramebuffer(this.fbo);
      this.gl.deleteTexture(this.texDepth);
      this.gl.deleteTexture(this.texColor);
      this.fbo = undefined;
    }
  }
  
  update(gl, width, height) {
    gl = this.gl = gl === undefined ? this.gl : gl;
    
    if (width !== this.size[0] || height !== this.size[1]) {
      console.log("fbo update", width, height);
      this.size[0] = width;
      this.size[1] = height;
      
      this.destroy();
      this.create(gl);
    }
  }
}

export class FrameStage extends FBO {
  constructor(shader, width=512, height=512) {
    super(undefined, width, height);
    
    this.shader = shader;
  }
  
  update(gl, width, height) {
    if (gl === undefined || width === undefined || height === undefined) {
      console.log("bad arguments to fbo.FrameStage.update()", arguments);
      throw new Error("bad arguments to fbo.FrameStage.update()");
    }
    
    super.update(gl, width, height);
  }
}

let BlitShader = {
  vertex : `
precision mediump float;

uniform sampler2D rgba;
uniform sampler2D depth;

attribute vec3 position;
attribute vec2 uv;

varying vec2 v_Uv;

void main(void) {
gl_Position = vec4(position, 1.0);
v_Uv = uv;
}
    
  `,
  fragment : `
#extension GL_EXT_frag_depth : require

precision mediump float;


uniform sampler2D rgba;
uniform sampler2D depth;

varying vec2 v_Uv;

void main(void) {
gl_FragColor = texture2D(rgba, v_Uv);
gl_FragDepthEXT = texture2D(depth, v_Uv)[0];
}

  `,
  uniforms : {},
  attributes : ["position", "uv"]
}

export class FramePipeline {
  constructor(width=512, height=512) {
    this.stages = [new FrameStage()];
    this.size = [width, height];
    this.smesh = undefined;
    
    this._texs = [
      new webgl.Texture(0),
      new webgl.Texture(1),
      new webgl.Texture(2),
      new webgl.Texture(3),
      new webgl.Texture(4),
      new webgl.Texture(5)
    ];
  }
  
  destroy(gl) {
    for (let stage of this.stages) {
      stage.destroy(gl);
    }
    
    if (this.smesh !== undefined) {
      this.smesh.destroy(gl);
      this.smesh = undefined;
    }
    this.stages = undefined;
  }
  
  //see webgl.getShader for shaderdef, ignore old loadShader cruft
  addStage(gl, shaderdef) {
    let shader = webgl.getShader(gl, shaderdef);
    let stage = new FrameStage(shader, this.size[0], this.size[1]);
    
    this.stages.push(stage);
    return stage;
  }
  
  draw(gl, drawfunc, width, height, drawmats) {
    if (this.smesh === undefined || this.size[0] != width || this.size[1] != height) {
      this.size[0] = width;
      this.size[1] = height;
      
      console.log("updateing framebuffer pipeline for new width/height");
      
      let lf = simplemesh.LayerTypes;
      this.smesh = new simplemesh.SimpleMesh(lf.LOC | lf.UV);
      this.smesh.program = this.blitshader = webgl.getShader(gl, BlitShader);
      this.smesh.uniforms.iprojectionMatrix = drawmats.irendermat;
      this.smesh.uniforms.projectionMatrix = drawmats.rendermat;
      
      let quad = this.smesh.quad([-1,-1,0], [-1,1,0], [1,1,0], [1,-1,0])
      quad.uvs([0,0,0], [0,1,0], [1,1,0], [1,0,0]);
    }
    
    //do first stage
    let stage = this.stages[0];
    
    stage.update(gl, width, height);
    stage.bind(gl);
    
    gl.viewport(0, 0, this.size[0], this.size[1]);
    
    gl.enable(gl.DEPTH_TEST);
    
    gl.clearDepth(1000000.0);
    gl.clearColor(0.3, 0.4, 1.0, 0.0);
    gl.clear(gl.COLOR_BUFFER_BIT | gl.DEPTH_BUFFER_BIT);
    
    drawfunc(gl);
    let laststage = stage;

    gl.depthMask(1);
    gl.disable(gl.DEPTH_TEST);
    
    for (let i=1; i<this.stages.length; i++) {
      let stage = this.stages[i];
      
      stage.update(gl, width, height);
      
      stage.shader.uniforms.rgba = this._texs[0];
      this._texs[0].texture = laststage.texColor;
      
      stage.shader.uniforms.depth = this._texs[1];
      this._texs[1].texture = laststage.texDepth;
      
      stage.shader.uniforms.size = this.size;

      this.smesh.program = stage.shader;
      stage.bind(gl);
      
      this.smesh.draw(gl);
      
      laststage = stage;
    }
  }
  
  drawFinal(gl, stage=undefined) {
    if (stage === undefined) {
      stage = this.stages[this.stages.length-1];
    }
    
    gl.bindFramebuffer(gl.FRAMEBUFFER, null);
    gl.disable(gl.DEPTH_TEST);
    gl.depthMask(1);
    
    this.smesh.program = this.blitshader;
    this.blitshader.uniforms.rgba = this._texs[0];
    this.blitshader.uniforms.depth = this._texs[1];
    this.blitshader.uniforms.size = this.size;
    
    this._texs[0].texture = stage.texColor;
    this._texs[1].texture = stage.texDepth;
    
    this.smesh.draw(gl);
  }
}

let _graph = undefined;

import {Matrix4, Vector2, Vector3, Vector4} from '../util/vectormath.js';
