export class Matrix4Socket extends NodeSocketType {
  constructor(uiname, flag) {
    super(uiname, flag);
    
    this.value = new Matrix4();
  }
  
  static nodedef() {return {
    name : "mat4",
    uiname : "Matrix",
    color : [1,,0.5,0.25,1]
  }}
  
  copy() {
    let ret = new Matrix4Socket(this.uiname, this.flag);
    this.copyTo(ret);
    return ret;
  }
  
  copyTo(b) {
    super.copyTo(b);
    
    b.value.load(this.value);
  }
  
  cmpValue(b) {
    return -1;
  }
  
  copyValue() {
      return new Matrix4(this.value);
  }
  
  diffValue(b) {
    let m1 = this.value.$matrix;
    let m2 = b.$matrix;
    
    let diff = 0.0, tot=0.0;
    
    for (let k in m1) {
      let a = m1[k], b = m2[k];
      
      diff += Math.abs(a-b);
      tot += 1.0;
    }
    
    return tot != 0.0 ? diff / tot : 0.0;
  }
  
  getValue() {
    return this.value;
  }
  
  setValue(val) {
    this.value.load(val);
  }
  static fromSTRUCT(reader) {
    let ret = new Matrix4Socket();
    reader(ret);
    return ret;
  }
