  static fromSTRUCT(reader) {
    let ret = new Scene();
    
    reader(ret);
    ret.afterSTRUCT();

    ret.objects.onselect = ret._onselect.bind(ret);

    return ret;
  }
  
  dataLink(getblock, getblock_us) {
    this.objects.dataLink(this, getblock, getblock_us);

    delete this.active;
  }
