    }

    var data = this.mesh.tri_ids
    var i = this.i*3; //*3 is because triangles have three vertices
    
    _ids_arrs[0][0] = i1, i1 = _ids_arrs[0];
    _ids_arrs[1][0] = i2, i2 = _ids_arrs[1];
    _ids_arrs[2][0] = i3, i3 = _ids_arrs[2];
    
    data.copy(i, i1);
    data.copy(i+1, i2);
    data.copy(i+2, i3);
    
    return this;
  }
}

export class QuadEditor {
    constructor() {
      this.t1 = new TriEditor();
      this.t2 = new TriEditor();
    }
    
    bind(mesh, i) {
      this.t1.bind(mesh, i);
      this.t2.bind(mesh, i+1);
      
      return this;
    }
    
    uvs(u1, u2, u3, u4) {
      this.t1.uvs(u1, u2, u3);
      this.t2.uvs(u1, u3, u4);
      
      return this;
    }
    
    colors(u1, u2, u3, u4) {
      this.t1.colors(u1, u2, u3);
      this.t2.colors(u1, u3, u4);
      
      return this;
    }
    
    normals(u1, u2, u3, u4) {
      this.t1.normals(u1, u2, u3);
      this.t2.normals(u1, u3, u4);
      
      return this;
    }
    
    ids(u1, u2, u3, u4) {
      this.t1.ids(u1, u2, u3);
      this.t2.ids(u1, u3, u4);
      
      return this;
    }
}

export class LineEditor {
  constructor() {
    this.mesh = undefined;
    this.i = 0;
  }
  
  bind(mesh, i) {
    this.mesh = mesh;
    this.i = i;
    return this;
  }
  
  colors(c1, c2) {
    var data = this.mesh.line_colors;
    var i = this.i*2; //*3 is because triangles have three vertices
    
    data.copy(i, c1);
    data.copy(i+1, c2);
    
    return this;
  }  
  
  uvs(c1, c2) {
    var data = this.mesh.line_uvs;
    var i = this.i*2; //*3 is because triangles have three vertices
    
    data.copy(i, c1);
    data.copy(i+1, c2);
    
    return this;
  }  
  
  ids(i1, i2) {
    if (i1 === undefined || i2 === undefined) {
      throw new Error("i1/i2 cannot be undefined");
    }

    var data = this.mesh.line_ids;
    var i = this.i*2; //*3 is because triangles have three vertices
    
    _ids_arrs[0][0] = i1, i1 = _ids_arrs[0];
    _ids_arrs[1][0] = i2, i2 = _ids_arrs[1];
    
    data.copy(i, _ids_arrs[0]);
    data.copy(i+1, _ids_arrs[1]);
    
    return this;
  }  
}

export class PointEditor {
  constructor() {
    this.mesh = undefined;
    this.i = 0;
  }

  bind(mesh, i) {
    this.mesh = mesh;
    this.i = i;
    return this;
  }

  colors(c1) {
    var data = this.mesh.point_colors;
    var i = this.i;

    data.copy(i, c1);

    return this;
  }

  normals(c1) {
    var data = this.mesh.point_normals;
    var i = this.i;

    data.copy(i, c1);

    return this;
  }

  uvs(c1) {
    var data = this.mesh.point_uvs;
    var i = this.i;

    data.copy(i, c1);

    return this;
  }

  ids(i1) {
    if (i1 === undefined) {
      throw new Error("i1 cannot be undefined");
    }

    var data = this.mesh.point_ids;
    var i = this.i;

    _ids_arrs[0][0] = i1, i1 = _ids_arrs[0];

    data.copy(i, _ids_arrs[0]);

    return this;
  }
}

export class GeoLayer extends Array {
  constructor(size, name, type, idx) { //idx is for different layers of same type, e.g. multiple uv layers
    super();
    
    this.type = type;
    this.data_f32 = [];
    
    this.size = size;
    this.name = name;
    
    this.idx = idx;
    this.id = undefined;
  }
  
  extend(data) {
    var tot = this.size;
    var starti = this.length;
    
    for (var i=0; i<tot; i++) {
      this.push(0);
    }
    
    if (data != undefined) {
      this.copy(~~(starti/this.size), data, 1);
    }
    
    return this;
  }
  
  //i and n will be multiplied by .size
  copy(i, data, n) {
    if (n == undefined) n = 1;
    
    var tot = n*this.size;
    
    i *= this.size;
    
    var di = 0;
    var end = i + tot;
    while (i < end) {
      this[i] = data[di];
      di++;
      i++;
    }
  }
  
  [Symbol.keystr]() {
    return "" + this.id;
  }
}

export class GeoLayerMeta {
  constructor(type) {
    this.type = type;
    this.layers = [];
  }
}

export class GeoLayerManager {
  constructor() {
    this.layers = new util.set();
    this.layer_meta = {};
    this.layer_idgen = new util.IDGen();
  }
  
  get_meta(type) {
    if (!(type in this.layer_meta)) {
      this.layer_meta[type] = new GeoLayerMeta(type);
    }
    
    return this.layer_meta[type];
  }
  
  [Symbol.iterator]() {
    return this.layers[Symbol.iterator]();
  }
  
  get(name, type, size, idx) {
     if (size == undefined) {
       size = TypeSizes[type];
     }
     
     var meta = this.get_meta(type);
     
     if (idx == undefined)
       idx = meta.layers.length;
     
     var layer = new GeoLayer(size, name, type, idx);
     layer.id = this.layer_idgen.next();
     
     
     this.layers.add(layer);
     meta.layers.push(layer);
     
     return layer;
  }
}

var _default_uv = [0, 0];
var _default_color = [0, 0, 0, 1];
var _default_normal = [0, 0, 1];
var _default_id = [-1];

export class SimpleIsland {
  constructor() {
    var lay = this.layers = new GeoLayerManager();
    
    this.tri_cos    = lay.get("tri_cos", LayerTypes.LOC); //array
    this.tri_normals = lay.get("tri_normals", LayerTypes.NORMAL); //array
    this.tri_uvs    = lay.get("tri_uvs", LayerTypes.UV); //array
    this.tri_colors = lay.get("tri_colors", LayerTypes.COLOR); //array
    this.tri_ids    = lay.get("tri_ids", LayerTypes.ID); //array
    
    this.line_cos    = lay.get("line_cos", LayerTypes.LOC); //array
    this.line_normals = lay.get("line_normals", LayerTypes.NORMAL); //array
    this.line_uvs    = lay.get("line_uvs", LayerTypes.UV); //array
    this.line_colors = lay.get("line_colors", LayerTypes.COLOR); //array
    this.line_ids    = lay.get("line_ids", LayerTypes.ID); //array

    this.point_cos    = lay.get("point_cos", LayerTypes.LOC); //array
    this.point_normals = lay.get("point_normals", LayerTypes.NORMAL); //array
    this.point_uvs    = lay.get("point_uvs", LayerTypes.UV); //array
    this.point_colors = lay.get("point_colors", LayerTypes.COLOR); //array
    this.point_ids    = lay.get("point_ids", LayerTypes.ID); //array

    this.totpoint = 0;
    this.totline = 0;
    this.tottri = 0;

    this.layerflag = undefined;
    
    this.regen = 1;
    
    this.tri_editors = util.cachering.fromConstructor(TriEditor, 32);
    this.quad_editors = util.cachering.fromConstructor(QuadEditor, 32);
    this.line_editors = util.cachering.fromConstructor(LineEditor, 32);
    this.point_editors = util.cachering.fromConstructor(PointEditor, 32);

    this.buffer = new RenderBuffer();
    this.program = undefined;
    
    this.textures = [];
    this.uniforms = {};
    this._uniforms_temp = {};
  }
  
  point(v1) {
    let i = this.totpoint;

    let cos = this.point_cos;
    cos.push(v1[0]); cos.push(v1[1]); cos.push(v1[2]);

    var layerflag = this.layerflag == undefined ? this.mesh.layerflag : this.layerflag;

    for (i=0; i<1; i++) {
      if (layerflag & LayerTypes.UV)
        this.point_uvs.extend(_default_uv);
      if (layerflag & LayerTypes.COLOR)
        this.point_colors.extend(_default_color);
      if (layerflag & LayerTypes.NORMAL)
        this.point_normals.extend(_default_normal);
      if (layerflag & layerflag.ID)
        this.point_ids.extend(_default_id);
    }

    this.totpoint++;
    return this.point_editors.next().bind(this, this.totpoint-1);
  }
  
  line(v1, v2) {
    let i = this.totline;
    
    let cos = this.line_cos;
    cos.push(v1[0]); cos.push(v1[1]); cos.push(v1[2]);
    cos.push(v2[0]); cos.push(v2[1]); cos.push(v2[2]);

    var layerflag = this.layerflag == undefined ? this.mesh.layerflag : this.layerflag;
    
    for (i=0; i<2; i++) {
      if (layerflag & LayerTypes.UV)
        this.line_uvs.extend(_default_uv);
      if (layerflag & LayerTypes.COLOR)
        this.line_colors.extend(_default_color);
      if (layerflag & LayerTypes.NORMAL)
        this.line_normals.extend(_default_normal);
      if (layerflag & layerflag.ID)
        this.line_ids.extend(_default_id);
    }
    
    this.totline++;
    return this.line_editors.next().bind(this, this.totline-1);
  }
  
  tri(v1, v2, v3) {
    var i = this.tottri;
    
    var cos = this.tri_cos;
    cos.push(v1[0]); cos.push(v1[1]); cos.push(v1[2]);
    cos.push(v2[0]); cos.push(v2[1]); cos.push(v2[2]);
    cos.push(v3[0]); cos.push(v3[1]); cos.push(v3[2]);
    
    var layerflag = this.layerflag == undefined ? this.mesh.layerflag : this.layerflag;
    
    for (var i=0; i<3; i++) {
      if (layerflag & LayerTypes.UV)
        this.tri_uvs.extend(_default_uv);
      if (layerflag & LayerTypes.COLOR)
        this.tri_colors.extend(_default_color);
      if (layerflag & LayerTypes.NORMAL)
        this.tri_normals.extend(_default_normal);
      if (layerflag & layerflag.ID)
        this.tri_ids.extend(_default_id);
    }
    
    this.tottri++;
    
    return this.tri_editors.next().bind(this, this.tottri-1);
  }
  
  quad(v1, v2, v3, v4) {
    var i = this.tottri;
    
    this.tri(v1, v2, v3);
    this.tri(v1, v3, v4);
    
    return this.quad_editors.next().bind(this, i);
  }
  
  destroy(gl) {
    this.buffer.destroy(gl);
    this.regen = true;
  }
  
  gen_buffers(gl) {
    var layerflag = this.layerflag == undefined ? this.mesh.layerflag : this.layerflag;
    
    for (var layer of this.layers) {
      if (!(layer.type & layerflag)) {
        continue;
      }
      
      var size = layer.size*layer.length;
      
      if (layer.data_f32 == undefined || layer.data_f32.length != size) {
        layer.data_f32 = new Float32Array(layer);
      }
      
      var buf = this.buffer.get(gl, layer.name);

      gl.bindBuffer(gl.ARRAY_BUFFER, buf);
      gl.bufferData(gl.ARRAY_BUFFER, layer.data_f32, gl.STATIC_DRAW);
    }
  }
  
  _draw_tris(gl, uniforms, params, program) {
    let li = 0;
    
    for (let i=0; i<6; i++) {
      gl.disableVertexAttribArray(i);
    }
    
    gl.enableVertexAttribArray(li);
    
    gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.tri_cos);
    gl.vertexAttribPointer(li, this.tri_cos.size, gl.FLOAT, false, 0, 0);
    
    var layerflag = this.layerflag == undefined ? this.mesh.layerflag : this.layerflag;
    
    if (layerflag & LayerTypes.NORMAL) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.tri_normals);
      gl.vertexAttribPointer(li, this.tri_normals.size, gl.FLOAT, true, 0, 0);
    }
    
    if (layerflag & LayerTypes.UV) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.tri_uvs);
      gl.vertexAttribPointer(li, this.tri_uvs.size, gl.FLOAT, false, 0, 0);
    } 
    
    if (layerflag & LayerTypes.COLOR) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.tri_colors);
      gl.vertexAttribPointer(li, this.tri_colors.size, gl.FLOAT, false, 0, 0);
    }
    
    if (layerflag & LayerTypes.ID) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.tri_ids);
      gl.vertexAttribPointer(li, this.tri_ids.size, gl.FLOAT, false, 0, 0);
    }
    
    gl.drawArrays(gl.TRIANGLES, 0, this.tottri*3);
  }

  _draw_points(gl, uniforms, params) {
    var layerflag = this.layerflag == undefined ? this.mesh.layerflag : this.layerflag;

    for (let i=0; i<6; i++) {
      gl.disableVertexAttribArray(i);
    }

    let li = 0;
    gl.enableVertexAttribArray(li);

    gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.point_cos);
    gl.vertexAttribPointer(li, this.point_cos.size, gl.FLOAT, false, 0, 0);

    if (layerflag & LayerTypes.NORMAL) {
      li++;

      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.point_normals);
      gl.vertexAttribPointer(li, this.point_normals.size, gl.FLOAT, true, 0, 0);
    }

    if (layerflag & LayerTypes.UV) {
      li++;

      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.point_uvs);
      gl.vertexAttribPointer(li, this.point_uvs.size, gl.FLOAT, false, 0, 0);
    }

    if (layerflag & LayerTypes.COLOR) {
      li++;

      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.point_colors);
      gl.vertexAttribPointer(li, this.point_colors.size, gl.FLOAT, false, 0, 0);
    }

    if (layerflag & LayerTypes.ID) {
      li++;

      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.point_ids);
      gl.vertexAttribPointer(li, this.point_ids.size, gl.FLOAT, false, 0, 0);
    }

    //console.log(this.totpoint, this.point_cos);
    gl.drawArrays(gl.POINTS, 0, this.totpoint);
  }

  _draw_lines(gl, uniforms, params) {
    var layerflag = this.layerflag == undefined ? this.mesh.layerflag : this.layerflag;

    for (let i=0; i<6; i++) {
      gl.disableVertexAttribArray(i);
    }
    
    let li = 0;
    gl.enableVertexAttribArray(li);
    
    gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.line_cos);
    gl.vertexAttribPointer(li, this.line_cos.size, gl.FLOAT, false, 0, 0);

    if (layerflag & LayerTypes.NORMAL) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.line_normals);
      gl.vertexAttribPointer(li, this.line_normals.size, gl.FLOAT, true, 0, 0);
    }
    
    if (layerflag & LayerTypes.UV) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.line_uvs);
      gl.vertexAttribPointer(li, this.line_uvs.size, gl.FLOAT, false, 0, 0);
    } 
    
    if (layerflag & LayerTypes.COLOR) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.line_colors);
      gl.vertexAttribPointer(li, this.line_colors.size, gl.FLOAT, false, 0, 0);
    }
    
    if (layerflag & LayerTypes.ID) {
      li++;
      
      gl.enableVertexAttribArray(li);
      gl.bindBuffer(gl.ARRAY_BUFFER, this.buffer.line_ids);
      gl.vertexAttribPointer(li, this.line_ids.size, gl.FLOAT, false, 0, 0);
    }
    
    //console.log(this.totline, this.line_cos);
    gl.drawArrays(gl.LINES, 0, this.totline*2);
  }
  
  draw(gl, uniforms, params, program_override=undefined) {
    var program = this.program == undefined ? this.mesh.program : this.program;

    if (program_override !== undefined) {
      program = program_override;
    }

    if (this.regen) {
      this.regen = 0;
      this.gen_buffers(gl);
    }
    
    if (uniforms == undefined) {
      for (var k in this._uniforms_temp) {
        delete this._uniforms_temp[k];
      }
      
      uniforms = this._uniforms_temp;
    }
    
    for (var k in this.uniforms) {
      if (!(k in uniforms)) {
        uniforms[k] = this.uniforms[k];
      }
    }
    
    for (var k in this.mesh.uniforms) {
      if (!(k in uniforms)) {
        uniforms[k] = this.mesh.uniforms[k];
      }
    }
    
    if (program == undefined)
      program = gl.simple_shader;
  
    program.bind(gl, uniforms);
    
    if (this.tottri) {
      this._draw_tris(gl, uniforms, params, program);
    }
    
    if (this.totline) {
      this._draw_lines(gl, uniforms, params, program);
    }

    if (this.totpoint) {
      this._draw_points(gl, uniforms, params, program);
    }

    if (gl.getError()) {
      this.regen = 1;
    }
  }
}

export class SimpleMesh {
  constructor(layerflag=LayerTypes.LOC|LayerTypes.NORMAL|LayerTypes.UV) {
    
    this.layerflag = layerflag;
    
    this.islands = [];
    this.uniforms = {};
    
    this.add_island();
    this.island = this.islands[0];
  }
  
  add_island() {
    var island = new SimpleIsland();
    
    island.mesh = this;
    this.island = island;
    
    this.islands.push(island);
    return island;
  }
  
  destroy(gl) {
    for (var island of this.islands) {
      island.destroy(gl);
    }
  }
  
  tri(v1, v2, v3) {
    return this.island.tri(v1, v2, v3);
  }
  
  quad(v1, v2, v3, v4) {
    return this.island.quad(v1, v2, v3, v4);
  }
  
  line(v1, v2) {
    return this.island.line(v1, v2);
  }

  point(v1) {
    return this.island.point(v1);
  }

  draw(gl, uniforms, program_override=undefined) {
    for (var island of this.islands) {
      island.draw(gl, uniforms, undefined, program_override);
    }
  }
}

"use strict";

import * as util from '../util/util.js';
import {Vector2, Vector3, Vector4, Quat, Matrix4} from '../util/vectormath.js';
import '../path.ux/scripts/struct.js';
let STRUCT = nstructjs.STRUCT;

export class IntUniform {
  constructor(val) {
    this.val = val;
  }
}

//params are passed to canvas.getContext as-is
export function init_webgl(canvas, params) {
  var gl = canvas.getContext("webgl", params);
  
  gl.getExtension("OES_texture_float");
  gl.getExtension("OES_standard_derivatives");
  gl.getExtension("ANGLE_instanced_arrays");
  gl.getExtension("WEBGL_lose_context");
  gl.getExtension("WEBGL_draw_buffers");
  gl.depth_texture = gl.getExtension("WEBGL_depth_texture");
  gl.getExtension("EXT_frag_depth");
  //gl.getExtension("WEBGL_debug_shaders");
  
  gl.shadercache = {};
  
  return gl;
}

function format_lines(script) {
  var i = 1;
  var lines = script.split("\n")
  var maxcol = Math.ceil(Math.log(lines.length) / Math.log(10))+1;
  
  var s = "";
  
  for (var line of lines) {
    s += ""+i + ":";
    while (s.length < maxcol) {
      s += " "
    }
    
    s += line + "\n";
    i++;
  }
  
  return s;
}

export function hashShader(sdef) {
  let hash;
  
  let clean = {
    vertex : sdef.vertex,
    fragment : sdef.fragment,
    uniforms : sdef.uniforms,
    attributes : sdef.attributes
  };
  
  let ret = JSON.stringify(clean);
  sdef.__hash = ret;
  
  return ret;
}


/*
shaderdef = {
  fragment : fragment shader code,
  vertex : vertex shader code,
  uniforms : uniforms,
  attributes : attributes
}
*/

export function getShader(gl, shaderdef) {
  if (gl.shadercache === undefined) {
    gl.shadercache = {};
  }
  
  let hash = shaderdef.__hash !== undefined ? shaderdef.__hash : hashShader(shaderdef);
  if (hash in gl.shadercache) {
    return gl.shadercache[hash];
  }
  
  let shader = new ShaderProgram(gl, shaderdef.vertex, shaderdef.fragment, shaderdef.attributes);
  if (shaderdef.uniforms)
    shader.uniforms = shaderdef.uniforms;
  
  gl.shadercache[hash] = shader;
  return shader;
}

//
// loadShader
//
// 'shaderId' is the id of a <script> element containing the shader source string.
// Load this shader and return the WebGLShader object corresponding to it.
//
function loadShader(ctx, shaderId)
{   
    var shaderScript = document.getElementById(shaderId);
    
    if (!shaderScript) {
      shaderScript = {text : shaderId, type : undefined};
      
      if (shaderId.trim().toLowerCase().startsWith("//vertex")) {
        shaderScript.type = "x-shader/x-vertex";
      } else if (shaderId.trim().toLowerCase().startsWith("//fragment")) {
        shaderScript.type = "x-shader/x-fragment";
      } else {
        console.trace();
        console.log("Invalid shader type");
        console.log("================");
        console.log(format_lines(shaderScript));
        console.log("================");
        throw new Error("Invalid shader type for shader script;\n script must start with //vertex or //fragment");
      }
    }

    if (shaderScript.type == "x-shader/x-vertex")
        var shaderType = ctx.VERTEX_SHADER;
    else if (shaderScript.type == "x-shader/x-fragment")
        var shaderType = ctx.FRAGMENT_SHADER;
    else {
        log("*** Error: shader script '"+shaderId+"' of undefined type '"+shaderScript.type+"'");
        return null;
    }

    // Create the shader object
    if (ctx == undefined || ctx == null || ctx.createShader == undefined)
      console.trace();
      
    var shader = ctx.createShader(shaderType);

    // Load the shader source
    ctx.shaderSource(shader, shaderScript.text);

    // Compile the shader
    ctx.compileShader(shader);

    // Check the compile status
    var compiled = ctx.getShaderParameter(shader, ctx.COMPILE_STATUS);
    if (!compiled && !ctx.isContextLost()) {
        // Something went wrong during compilation; get the error
        var error = ctx.getShaderInfoLog(shader);
        
        console.log(format_lines(shaderScript.text));
        console.log("\nError compiling shader: ", error);
        
        ctx.deleteShader(shader);
        return null;
    }

    return shader;
}

var _safe_arrays = [
  0,
  0,
  new Float32Array(2),
  new Float32Array(3),
  new Float32Array(4),
];

export class ShaderProgram {
  constructor(gl, vertex, fragment, attributes) {
    this.vertexSource = vertex;
    this.fragmentSource = fragment;
    this.attrs = [];
    
    for (var a of attributes) {
      this.attrs.push(a);
    }
    
    this.rebuild = 1;
    
    this.uniformlocs = {};
    this.attrlocs = {};
    this.uniform_defaults = {};
    
    this.uniforms = {};
    this.gl = gl;
  }
  
  init(gl) {
    this.gl = gl;
    this.rebuild = false;
    
    var vshader = this.vertexSource, fshader = this.fragmentSource;
    
    
    function loadShader(shaderType, code) {
        var shader = gl.createShader(shaderType);

        // Load the shader source
        gl.shaderSource(shader, code);

        // Compile the shader
        gl.compileShader(shader);

        // Check the compile status
        var compiled = gl.getShaderParameter(shader, gl.COMPILE_STATUS);
        if (!compiled && !gl.isContextLost()) {
            // Something went wrong during compilation; get the error
            var error = gl.getShaderInfoLog(shader);
            
            console.log(format_lines(code));
            console.log("\nError compiling shader: ", error);
            
            gl.deleteShader(shader);
            return null;
        }

        return shader;
    }

    // create our shaders
    var vertexShader = loadShader(gl.VERTEX_SHADER, vshader);
    var fragmentShader = loadShader(gl.FRAGMENT_SHADER, fshader);
    
    // Create the program object
    var program = gl.createProgram();

    // Attach our two shaders to the program
    gl.attachShader (program, vertexShader);
    gl.attachShader (program, fragmentShader);

    var attribs = this.attrs;
    
    // Bind attributes
    for (var i = 0; i < attribs.length; ++i)
        gl.bindAttribLocation (program, i, attribs[i]);

    // Link the program
    gl.linkProgram(program);

    // Check the link status
    var linked = gl.getProgramParameter(program, gl.LINK_STATUS);
    if (!linked && !gl.isContextLost()) {
        // something went wrong with the link
        var error = gl.getProgramInfoLog (program);
        console.log("Error in program linking:"+error);

        //do nothing
        //gl.deleteProgram(program);
        //gl.deleteProgram(fragmentShader);
        //gl.deleteProgram(vertexShader);

        return null;
    }
    
    console.log("created shader", program);

    this.program = program;

    this.gl = gl;
    this.vertexShader = vertexShader;
    this.fragmentShader = fragmentShader;
    this.attrs = [];
    
    this.attrlocs = {};
    this.uniformlocs = {};
    
    this.uniforms = {}; //default uniforms
    
    for (var i=0; i<attribs.length; i++) {
      this.attrs.push(i);
      this.attrlocs[attribs[i]] = i;
    }
  }
  
  //this function was originally asyncrounous
  static load_shader(scriptid, attrs) {
    var script = document.getElementById(scriptid);
    var text = script.text;

    var ret = new ShaderProgram(undefined, undefined, undefined, ["position", "normal", "uv", "color", "id"]);

    var lowertext = text.toLowerCase();
    var vshader = text.slice(0, lowertext.search("//fragment"));
    var fshader = text.slice(lowertext.search("//fragment"), text.length);

    ret.vertexSource = vshader;
    ret.fragmentSource = fshader;
    ret.ready = true;

    ret.promise = new Promise(function(accept, reject) {
      accept(ret);
    });

    ret.then = function() {
      return this.promise.then.apply(this.promise, arguments);
    }

    return ret;
  }
  
  on_gl_lost(newgl) {
    this.rebuild = 1;
    this.gl = newgl;
    this.program = undefined;
    
    this.uniformlocs = {};
  }
  
  uniformloc(name) {
    if (this.uniformlocs[name] == undefined) {
      this.uniformlocs[name] = this.gl.getUniformLocation(this.program, name);
    }
    
    return this.uniformlocs[name];
  }
  
  attrloc(name) {
    return this.attrlocs[name];
  }
  
  bind(gl, uniforms) {
    this.gl = gl;
    
    if (this.rebuild) {
      this.init(gl);
      
      if (this.rebuild) 
        return; //failed to initialize
    }
    
    function setv(dst, src, n) {
      for (var i=0; i<n; i++) {
        dst[i] = src[i];
      }
    }
    
    gl.useProgram(this.program);
    this.gl = gl;
    
    for (var i=0; i<2; i++) {
      var us = i ? uniforms : this.uniforms;
      for (var k in us) {
        var v = us[k];
        var loc = this.uniformloc(k)
        
        if (loc == undefined) {
            //stupid gl returns null if it optimized away the uniform,
            //so we must silently accept this
            //console.log("Warning, could not locate uniform", k, "in shader");
            continue;
        }
        
        if (v instanceof IntUniform) {
          gl.uniform1i(loc, v.val);
        } else if (v instanceof Texture) {
          v.bind(gl, this.uniformloc(k));
        } else if (v instanceof Array) {
          switch (v.length) {
            case 2:
              var arr = _safe_arrays[2];
              setv(arr, v, 2);
              
              gl.uniform2fv(loc, arr);
              break;
            case 3:
              var arr = _safe_arrays[3];
              setv(arr, v, 3);
              gl.uniform3fv(loc, arr);
              break;
            case 4:
              var arr = _safe_arrays[4];
              setv(arr, v, 4);
              gl.uniform4fv(loc, arr);
              break;
            default:
              console.log(v);
              throw new Error("invalid array");
              break;
          }
        } else if (v instanceof Matrix4) {
          //console.log("found matrix");
          v.setUniform(gl, loc);
        } else if (typeof v == "number") { 
          gl.uniform1f(loc, v);
        } else {
          throw new Error("Invalid uniform");
        }
      }
    }
    
    return this;
  }
}

export class RenderBuffer {
  constructor() {
    this._layers = {};
  }
  
  get(gl, name) {
    if (this[name] != undefined) {
      return this[name];
    }
    
    var buf = gl.createBuffer();
    
    this._layers[name] = buf;
    this[name] = buf;
    
    return buf;
  }
  
  destroy(gl, name) {
    if (name == undefined) {
      for (var k in this._layers) {
        gl.deleteBuffer(this._layers[k]);
        
        this._layers[k] = undefined;
        this[k] = undefined;
      }
    } else {
      if (this._layers[name] == undefined) {
        console.trace("WARNING: gl buffer no in RenderBuffer!", name, gl);
        return;
      }
      
      gl.deleteBuffer(this._layers[name]);
      
      this._layers[name] = undefined;
      this[name] = undefined;
    }
  }
}

export class Texture {
  constructor(texture_slot, texture) {
    this.texture = texture;
    this.texture_slot = texture_slot;
  }
  
  static load(gl, width, height, data) {
    let tex = gl.createTexture();
    
    gl.bindTexture(gl.TEXTURE_2D, tex);
    gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, width, height, 0, gl.RGBA, gl.UNSIGNED_BYTE, data);
    Texture.defaultParams(gl, tex);
    
    return new Texture(0, tex);
  }
  
  static defaultParams(gl, tex) {
    gl.bindTexture(gl.TEXTURE_2D, tex);
    
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.NEAREST);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.NEAREST);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
    
  }
  
  bind(gl, uniformloc) {
    gl.activeTexture(gl.TEXTURE0 + this.texture_slot);
    gl.bindTexture(gl.TEXTURE_2D, this.texture);
    gl.uniform1i(uniformloc, this.texture_slot);
  }
}

//cameras will derive from this class
export class DrawMats {
  constructor() {
    this.cameramat = new Matrix4();
    this.persmat = new Matrix4();
    this.rendermat = new Matrix4();
    this.normalmat = new Matrix4();
    
    this.icameramat = new Matrix4();
    this.ipersmat = new Matrix4();
    this.irendermat = new Matrix4();
    this.inormalmat = new Matrix4();
  }
  
  regen_mats(aspect=this.aspect) {
    this.aspect = aspect;
    
    this.rendermat.load(this.persmat).multiply(this.cameramat);
    this.normalmat.load(this.cameramat).makeRotationOnly();
    
    this.icameramat.load(this.cameramat).invert();
    this.ipersmat.load(this.persmat).invert();
    this.irendermat.load(this.rendermat).invert();
    this.inormalmat.load(this.normalmat).invert();
    
    return this;
  }
  
  toJSON() {
    return {
      cameramat  : this.cameramat.getAsArray(),
      persmat    : this.persmat.getAsArray(),
      rendermat  : this.rendermat.getAsArray(),
      normalmat  : this.normalmat.getAsArray(),
      
      icameramat : this.icameramat.getAsArray(),
      ipersmat   : this.ipersmat.getAsArray(),
      irendermat : this.irendermat.getAsArray(),
      inormalmat : this.inormalmat.getAsArray()
    }
  }
  
  loadJSON(obj) {
    this.cameramat.load(obj.cameramat);
    this.persmat.load(obj.persmat);
    this.rendermat.load(obj.rendermat);
    this.normalmat.load(obj.normalmat);
    
    this.icameramat.load(obj.icameramat);
    this.ipersmat.load(obj.ipersmat);
    this.irendermat.load(obj.irendermat);
    this.inormalmat.load(obj.inormalmat);
    
    return this;
  }
  
  static fromSTRUCT(reader) {
    let ret = new DrawMats();
    
    reader(ret);
    
    return ret;
  }
}
