import {Vector3, Vector2} from '../../util/vectormath.js';
import {FBO, FrameStage, FramePipeline} from "../../core/fbo.js";
import {Shaders} from './view3d_shaders.js';
import * as util from '../../util/util.js';

let _cache = {};

export class GPUSelectBuffer {
  constructor() {
    this.regen = true;
    this.pos = new Vector2([0, 0]);
    this.size = new Vector2([0, 0]);
  }

  dirty() {
    this.regen = true;
  }

  gen(ctx, gl, view3d) {
    if (this.fbo === undefined) {
      this.fbo = new FBO(gl, ~~this.size[0], ~~this.size[1]);
    }

    this.fbo.bind(gl);

    let uniforms = {
      projectionMatrix : view3d.camera.rendermat,
      objectMatrix : undefined
    };

    for (let ob of ctx.scene.objects.editable) {
      uniforms.objectMatrix = ob.outputs.matrix.getValue();

      for (let ed of view3d.editors) {
        ed.drawIDs(gl, uniforms, ob, ob.data, 0);
      }
    }

    this.fbo.unbind(gl);
  }

  sampleBlock(ctx, gl, view3d, x, y, w=16, h=16) {
    try {
      return this.sampleBlock_intern(ctx, gl, view3d, x, y, w, h);
    } catch (error) {
      util.print_stack(error);
      console.log("error in sampleBlock");

      gl.bindFramebuffer(gl.FRAMEBUFFER, null);
      return undefined;
    }
  }

  getSearchOrder(n) {
    if (n in _cache) {
      return _cache[n];
    }

    let ret = _cache[n] = [];

    for (let i=0; i<n*n; i++) {
      ret.push(i);
    }

    ret.sort((a, b) => {
      let x1 = a % n, y1 = ~~(a / n);
      let x2 = b % n, y2 = ~~(b / n);

      x1 -= n*0.5; y1 -= n*0.5;
      x2 -= n*0.5; y2 -= n*0.5;

      let w1 = /*Math.atan2(y1, x1) */ (x1*x1 + y1*y1);
      let w2 = /*Math.atan2(y2, x2) */ (x2*x2 + y2*y2);

      return w1-w2;
    });

    console.log("RET", ret);
    return ret;
  }
  sampleBlock_intern(ctx, gl, view3d, x, y, w=16, h=16) {
    if (this.pos.vectorDistance(view3d.pos) != 0.0 ||
        this.size.vectorDistance(view3d.size) != 0.0) {
      this.pos.load(view3d.pos);
      this.size.load(view3d.size);

      this.dirty();
    }

    if (this.regen) {
      this.gen(ctx, gl, view3d);
    }

    this.fbo.bind(gl);

    let data = new Float32Array(w*h*4);
    //gl.readPixels(x, y , w, h, gl.RGBA, gl.FLOAT, data);
    gl.readPixels(x, (~~this.size[1]) - (y + h), w, h, gl.RGBA, gl.FLOAT, data);

    this.fbo.unbind(gl);

    return data;
  }
};

import {Matrix4} from '../../util/vectormath.js';

export let PolygonOffset = {
  pre  : `uniform float polygonOffset;`,
  vertex : (posname) => `
  {
    float z = ${posname}[2];
    z -= polygonOffset*0.0001;
    
    ${posname}[2] = z;
  }
  `,
  fragment : `
  `
};

export let BasicLineShader = {
  vertex : `precision mediump float;
  
uniform mat4 projectionMatrix;
uniform mat4 normalMatrix;

attribute vec3 position;
attribute vec2 uv;
attribute vec4 color;

varying vec4 vColor;
varying vec2 vUv;

void main() {
  vec4 p = projectionMatrix * vec4(position, 1.0);
  
  gl_Position = p;
  vColor = color;
  vUv = uv;
}

  `,
  
  fragment : `precision mediump float;
uniform float alpha;

varying vec4 vColor;
varying vec2 vUv;

void main() {
  gl_FragColor = vColor * vec4(1.0, 1.0, 1.0, alpha);
}
  `,
  
  uniforms : {
    alpha : 1.0
  },
  
  attributes : [
    "position", "uv", "color"
  ]
};

export let BasicLitMesh = {
  vertex : `precision mediump float;
  
uniform mat4 projectionMatrix;
uniform mat4 objectMatrix;
uniform mat4 normalMatrix;

attribute vec3 position;
attribute vec3 normal;
attribute vec2 uv;
attribute vec4 color;

varying vec4 vColor;
varying vec3 vNormal;
varying vec2 vUv;

void main() {
  //vec4 p = objectMatrix * vec4(position, 1.0);
  
  vec4 p = projectionMatrix * vec4(position.xyz, 1.0);
  vec4 n = normalMatrix * vec4(normal, 0.0);
  
  gl_Position = p;
  
  vUv = uv;
  vNormal = n.xyz;
  vColor = color;
}

  `,
  
  fragment : `precision mediump float;
uniform float alpha;

varying vec4 vColor;
varying vec3 vNormal;
varying vec2 vUv;

void main() {
  vec4 c = vec4(1.0,1.0,1.0,1.0);//vColor;
  
  float f;
  
  f = abs(vNormal[1]*0.4 + vNormal[2]*0.6);
  c.rgb = vec3(f, f, f)*1.5;//*vColor.rgb;
  
  gl_FragColor = c;
}
  `,
  
  uniforms : {
    alpha : 1.0,
    objectMatrix : new Matrix4()
  },
  
  attributes : [
    "position", "normal", "uv", "color"
  ]
};

export let MeshEditShader = {
  vertex : `precision mediump float;
  
uniform mat4 projectionMatrix;
uniform mat4 objectMatrix;
uniform mat4 normalMatrix;

attribute vec3 position;
//attribute vec3 normal;
//attribute vec2 uv;
attribute vec4 color;
attribute float id;

uniform float alpha;
uniform float active_id;
uniform float highlight_id;
uniform float last_id;
uniform vec4 active_color;
uniform vec4 last_color;
uniform vec4 highlight_color;
uniform float pointSize;

varying vec4 vColor;
varying float vId;
${PolygonOffset.pre}

void main() {
  //vec4 p = objectMatrix * vec4(position, 1.0);
  
  vec4 p = projectionMatrix * vec4(position.xyz, 1.0);
  
  ${PolygonOffset.vertex("p")}
  
  gl_Position = p;
  gl_PointSize = pointSize;
  
  vId = id;
  
  if (highlight_id == id) {
    vColor = highlight_color;
  } else if (last_id == id) {
    vColor = last_color;
  } else if (active_id == id) {
    vColor = active_color;
  } else {
    vColor = color;
  } 
}
`,
  fragment : `precision mediump float;
uniform float alpha;
varying vec4 vColor;
varying float vId;
${PolygonOffset.pre}

void main() {
  vec4 c = vColor;
  
  ${PolygonOffset.fragment}
  gl_FragColor = c * vec4(1.0, 1.0, 1.0, alpha);
}
  `,

  uniforms : {
    alpha : 1.0,
    objectMatrix : new Matrix4()
  },

  attributes : [
    "position", "color", "id"
  ]
};

export let MeshIDShader = {
  vertex : `precision mediump float;
  
uniform mat4 projectionMatrix;
uniform mat4 objectMatrix;
uniform mat4 normalMatrix;

attribute vec3 position;
//attribute vec3 normal;
//attribute vec2 uv;
attribute vec4 color;
attribute float id;

uniform float id_offset;
uniform float object_id;
uniform float alpha;
uniform float active_id;
uniform float highlight_id;
uniform float last_id;
uniform vec4 active_color;
uniform vec4 last_color;
uniform vec4 highlight_color;
uniform float pointSize;

varying vec4 vColor;
varying float vId;
${PolygonOffset.pre}

void main() {
  //vec4 p = objectMatrix * vec4(position, 1.0);
  
  vec4 p = projectionMatrix * vec4(position.xyz, 1.0);
  
  ${PolygonOffset.vertex("p")}
  
  gl_Position = p;
  gl_PointSize = pointSize;
  
  vId = id + id_offset;
  
  vec4 c;
  
  c[0] = vId;
  c[1] = object_id;
  
  vColor = c;
}
`,
  fragment : `precision mediump float;

uniform float id_offset;
uniform float object_id;

varying vec4 vColor;
varying float vId;
${PolygonOffset.pre}

void main() {
  gl_FragColor = vec4(vId+1.0, object_id, 0.0, 1.0);
  ${PolygonOffset.fragment}
}
  `,

  uniforms : {
    objectMatrix : new Matrix4()
  },

  attributes : [
    "position", "color", "id"
  ]
};

export const ShaderDef = {
