    this.range = obj.range;
    return this;
  }
  
  setValue(val) {
    this.data = Math.floor(val);
    
    //fire events
    super.setValue(val);
    
    return this;
  }
  
  setStep(step) {
    super.setStep(Math.floor(step));
  }

  setRange(min, max) {
    if (min === undefined || max === undefined) {
      throw new Error("min and/or max cannot be undefined");
    }
    
    super.setRange(Math.floor(min), Math.floor(max));
    return this;
