*/

function bez3(a, b, c, t) {
  var r1 = a + (b - a)*t;
  var r2 = b + (c - b)*t;
  
  return r1 + (r2 - r1)*t;
}

function bez4(a, b, c, d, t) {
  var r1 = bez3(a, b, c, t);
  var r2 = bez3(b, c, d, t);
  
  return r1 + (r2 - r1)*t;
}

export function binomial(n, i) {
  if (i > n) {
    throw new Error("Bad call to binomial(n, i), i was > than n");
  }
  
  if (i == 0 || i == n) {
    return 1;
  }
  
  var key = "" + n + "," + i;
  
  if (key in bin_cache) 
    return bin_cache[key];
  
  var ret = binomial(n-1, i-1) + bin(n-1, i);
  bin_cache[key] = ret;
  
  return ret;
}
window.bin = binomial;

