    let style = document.createElement("style");
    style.textContent = `
        .menucon {
          position:absolute;
          float:left;
          
          display: block;
          -moz-user-focus: normal;
        }
        
        ul.menu {
          display : block;
          
          margin : 0px;
          padding : 0px;
          border-style : solid;
          border-width : 1px;
          border-color: grey;
          -moz-user-focus: normal;
          background-color: ${this.getDefault("MenuBG")};
