  start_fancy(prepend, setActive=true) {
    console.log("menu fancy start");

    let dom2 = document.createElement("div");
    //let dom2 = document.createElement("div");

    this.dom.setAttribute("class", "menu");
    dom2.setAttribute("class", "menu");

    let sbox = document.createElement("textbox-x");

