export class cachering extends Array {
  constructor(func, size) {
    super()
    
    this.cur = 0;
    
    for (var i=0; i<size; i++) {
      this.push(func());
    }
  }
  
  static fromConstructor(cls, size) {
    var func = function() {
      return new cls();
    }
    
    return new cachering(func, size);
  }
  
  next() {
    var ret = this[this.cur];
    this.cur = (this.cur+1)%this.length;
    
    return ret;
  }
