export class SetIter {
  constructor(set) {
    this.set = set;
    this.i   = 0;
    this.ret = {done : false, value : undefined};
  }
  
  [Symbol.iterator]() {
    return this;
  }
  
  next() {
    var ret = this.ret;

    while (this.i < this.set.items.length && this.set.items[this.i] === EmptySlot) {
      this.i++;
    }
    
    if (this.i >= this.set.items.length) {
      ret.done = true;
      ret.value = undefined;
      
      return ret;
    }
    
    
    ret.value = this.set.items[this.i++];
    return ret;
  }
}

export class set {
  constructor(input) {
    this.items = [];
    this.keys = {};
    this.freelist = [];
    
    this.length = 0;
    
    if (typeof input == "string") {
      input = new String(input);
    }
    
    if (input != undefined) {
      if (Symbol.iterator in input) {
        for (var item of input) {
          this.add(item);
        }
      } else if ("forEach" in input) {
        input.forEach(function(item) {
          this.add(item);
        }, this);
      } else if (input instanceof Array) {
        for (var i=0; i<input.length; i++) {
          this.add(input[i]);
        }
      }
    }
  }
  
  [Symbol.iterator]() {
    return new SetIter(this);
  }

  clear() {
    this.items.length = 0;
    this.keys = {};
    this.freelist.length = 0;
    this.length = 0;

    return this;
  }

  add(item) {
    var key = item[Symbol.keystr]();
    
    if (key in this.keys) return;
    
    if (this.freelist.length > 0) {
      var i = this.freelist.pop();
      
      this.keys[key] = i;
      this.items[i] = item;
    } else {
      var i = this.items.length;
      
      this.keys[key] = i;
      this.items.push(item);
    }
    
    this.length++;
  }
  
  remove(item, ignore_existence) {
    var key = item[Symbol.keystr]();
    
    if (!(key in this.keys)) {
      if (!ignore_existence) {
        console.trace("Warning, item", item, "is not in set");
      }
      return;
    }
    
    var i = this.keys[key];
    this.freelist.push(i);
    this.items[i] = EmptySlot;
    
    delete this.keys[key];
    
    this.length--;
  }
  
  has(item) {
    return item[Symbol.keystr]() in this.keys;
  }
  
  forEach(func, thisvar) {
    for (var i=0; i<this.items.length; i++) {
      var item = this.items[i];
      
      if (item === EmptySlot) 
        continue;
        
      thisvar != undefined ? func.call(thisvar, item) : func(item);
    }
  }
}

export class HashIter {
  constructor(hash) {
    this.hash = hash;
    this.i = 0;
    this.ret = {done : false, value : undefined};
  }
  
  next() {
    var items = this.hash._items;
    
    if (this.i >= items.length) {
      this.ret.done = true;
      this.ret.value = undefined;
      
      return this.ret;
    }
    
    do {
      this.i += 2;
    } while (this.i < items.length && items[i] === _hash_null);
    
    return this.ret;
  }
}

var _hash_null = {};
export class hashtable {
  constructor() {
    this._items = [];
    this._keys = {};
    this.length = 0;
  }
  
  [Symbol.iterator]() {
    return new HashIter(this);
  }
  
  set(key, val) {
    var key2 = key[Symbol.keystr]();
    
    var i;
    if (!(key2 in this._keys)) {
      i = this._items.length;
      
      try {
        this._items.push(0);
        this._items.push(0);
      } catch(error) {
        console.log(":::", this._items.length, key, key2, val)
        throw error;
      }
      
      this._keys[key2] = i;
      this.length++;
    } else {
      i = this._keys[key2];
    }
    
    this._items[i] = key;
    this._items[i+1] = val;
  }
  
  remove(key) {
    var key2 = key[Symbol.keystr]();
    
    if (!(key2 in this._keys)) {
      console.trace("Warning, key not in hashtable:", key, key2);
      return;
    }
    
    var i = this._keys[key2];
    
    this._items[i] = _hash_null;
    this._items[i+1] = _hash_null;
    
    delete this._keys[key2];
    this.length--;
  }
  
  has(key) {
    var key2 = key[Symbol.keystr]();
    
    return key2 in this._keys;
  }
  
  get(key) {
    var key2 = key[Symbol.keystr]();
    
    if (!(key2 in this._keys)) {
      console.trace("Warning, item not in hash", key, key2);
      return undefined;
    }
    
    return this._items[this._keys[key2]+1];
  }
  
  add(key, val) {
    return this.set(key, val);
  }
  
  keys() {
    var ret = [];
    
    for (var i=0; i<this._items.length; i += 2) {
      var key = this._items[i];
      
      if (key !== _hash_null) {
        ret.push(key);
      }
    }
    
    return ret;
  }
  
  values() {
    var ret = [];
    
    for (var i=0; i<this._items.length; i += 2) {
      var item = this._items[i+1];
      
      if (item !== _hash_null) {
        ret.push(item);
      }
    }
    
    return ret;
  }
  
  forEach(cb, thisvar) {
    if (thisvar == undefined)
      thisvar = self;
    
    for (var k in this._keys) {
      var i = this._keys[k];
      
      cb.call(thisvar, k, this._items[i]);
    }
  }
}

