export function strhash(str) {
  var hash = 0;

  for (var i=0; i<str.length; i++) {
    var ch = str.charCodeAt(i);

    hash = (hash ^ ch);
    hash = hash < 0 ? -hash : hash;

    hash = (hash*232344 + 4323543) & ((1<<19)-1);
  }

  return hash;
}

