        end[i] = Math.floor(maxv[i]/csize);
    }
    for (var x=start[0]; x<=end[0]; x++) {
        for (var y=start[1]; y<=end[1]; y++) {
            for (var z=start[2]; z<=end[2]; z++) {
                var bset=this.hashlookup(x, y, z, true);
