  if (points.length==0)
    return false;
  for (var i=0; i<points.length; i++) {
      cent.add(points[i]);
  }
  cent.divideScalar(points.length);
  var w=0, totw=0;
  for (var i=0; i<points.length; i++) {
      var v1=points[i];
      var v2=points[(i+1)%points.length];
      if (!colinear(v1, v2, cent)) {
          w+=winding(v1, v2, cent);
          totw+=1;
      }
