            s = s.split(/\/\//);
            vi = parseInt(s[0]);
            vn = parseInt(s[1]);
            
            vi = vi >= 0 ? vi-1 : vs.length + vi;
            vn = vn >= 0 ? vn-1 : vs.length + vn;
